mapper.select:
SELECT * FROM (SELECT ROW_NUMBER()OVER(ORDER BY NAME ASC) AS RNUM, ID, NAME, EMAIL, CODE, AUTH, POINT FROM MEMBER
WHERE CODE LIKE #{code}||'\_'||'%' ESCAPE '\'
WHERE CODE = #{hospital}
ORDER BY NAME ASC) WHERE RNUM BETWEEN ${start} AND ${end}

mapper.insert:
INSERT INTO HOSPITAL(NAME, LOCATION, CODE)
VALUES(
	((SELECT NAME FROM HOSPITAL WHERE CODE=#{curCode})||'_'||#{name}),
	(SELECT LOCATION FROM HOSPITAL WHERE CODE=#{curCode}),
	#{code}
)

mapper.select:
SELECT COUNT(*)
FROM HOSPITAL
WHERE CODE=#{code}

mapper.update:
UPDATE MEMBER
SET AUTH=1
WHERE ID=#{id}

UPDATE MEMBER
SET AUTH=3
WHERE ID=#{id}

UPDATE MEMBER
SET AUTH=1
WHERE ID=#{id}

UPDATE MEMBER
SET AUTH=5, CODE=''
WHERE ID=#{id}

UPDATE MEMBER
SET CODE=#{code}
WHERE ID=#{id}

mapper.select:
SELECT AUTH
FROM MEMBER
WHERE ID=#{id}

mapper.update:
UPDATE PARTTIME
SET START_TIME=#{st}, END_TIME=#{et}
WHERE NAME=#{name} AND CODE=#{code}

UPDATE MEMBER
SET POINT=#{point}
WHERE CODE LIKE #{code}||'\_'||'%' ESCAPE '\'

mapper.select:
SELECT NVL(COUNT(*), 0) FROM MEMBER
WHERE CODE LIKE #{code}||'\_'||'%' ESCAPE '\'
WHERE CODE = #{hospital}
